import numpy as npimport jsonclass AverageMeter(object):    """Computes and stores the average and current value"""    def __init__(self, name, fmt=':f'):        self.name = name        self.fmt = fmt        self.reset()    def reset(self):        self.val = []        self.index = []        self.avg = 0        self.sum = 0        self.count = 0    def update(self, val, n=1):        self.val.append(val)        self.sum += val * n        self.count += n        self.avg = self.sum / self.count    def __str__(self):        fmtstr = '{name} {val' + self.fmt + '} ({avg' + self.fmt + '})'        return fmtstr.format(**self.__dict__)class RecordWriter(object):    def __init__(self, name):        self.name = name        self.record = {}    def update(self, name, value):        if name not in self.record.keys():            self.record[name] = []        self.record[name].extend(value)    def write(self):        dict = json.dumps(self.record)        with open(f"{self.name}.json", "w") as f:            f.write(dict)